    Joi.assert(req.body, CREATE_USER_SCHEMA);

    next();
  } catch (err) {
    next(err);
  }
}

export function validateUserUpdate(req, res, next) {
  try {
    Joi.assert(req.body, UPDATE_USER_SCHEMA);

    next();
  } catch (err) {
    next(err);
  }
}

